import React, { Component } from 'react';
import {Form, Input, imageUrl, Upload, Layout, DatePicker, TimePicker, Switch, Checkbox, Button, Radio} from 'element-react'
import 'element-theme-default';
import ReactDOM from 'react-dom';
import axios from 'axios';
import { Link } from 'react-router-dom';

class Create extends Component {

  // constructor() {
  //   super();
  //   this.state = {
  //     name: '',
  //     address: '',
  //     city: '',
  //     phone: '',
  //     email: ''
  //   };
  // }
  // onChange = (e) => {
  //   const state = this.state
  //   state[e.target.name] = e.target.value;
  //   this.setState(state);
  // }

  // onSubmit = (e) => {
  //   e.preventDefault();

  //   const { name, address, city, phone, email } = this.state;
  //   console.log(this.state)
  //   axios.post('events', { name, address, city, phone, email })
  //     .then((result) => {
  //       this.props.history.push("/events")
  //     });
  // }

  constructor(props) {
    super(props);

    this.state = {
      form: {
        id: '',
        userId:'',
        start_time: null,
        end_time: null,
        description: null,
        image: false,
        category: [],
        resource: '',
        desc: '',
        // imageUrl:''
      }

    };
  }

  onSubmit(e) {
    e.preventDefault();
  }

  onChange(key, value) {
    this.state.form[key] = value;
    const { name, address, city, phone, email } = this.state;
    console.log(this.state)
    axios.post('events', { name, address, city, phone, email })
      .then((result) => {
        this.props.history.push("/events")
      });
    this.forceUpdate();
  }

  handleAvatarScucess(res, file) {
    this.setState({ imageUrl: URL.createObjectURL(file.raw) });
  }

  render() {
    return (
      <div margin = "10">
      <Layout.Row type = "flex" className="row-bg" justify="space-around"> 
            <h2>Create Events</h2>
      </Layout.Row>
      <Layout.Row type = "flex" className="row-bg" justify="space-around"> 
      <Layout.Col span ="12">
      <Form model={this.state.form} labelWidth="80" onSubmit={this.onSubmit.bind(this)}>
        <Form.Item label="Name">
          <Input value={this.state.form.name} onChange={this.onChange.bind(this, 'name')}></Input>
        </Form.Item>
        <Form.Item label="Date">
          <Input value={this.state.form.date} onChange={this.onChange.bind(this, 'date')}></Input>
        </Form.Item>
        <Form.Item label="Start Time">
          <Input value={this.state.form.start_time} onChange={this.onChange.bind(this, 'start_time')}></Input>
        </Form.Item>
        <Form.Item label="End Time">
          <Input value={this.state.form.end_time} onChange={this.onChange.bind(this, 'end_time')}></Input>
        </Form.Item>
        <Form.Item label="Category">
          <Checkbox.Group value={this.state.form.category} onChange={this.onChange.bind(this, 'category')}>
            <Checkbox label="C1" name="category"></Checkbox>
            <Checkbox label="C2" name="category"></Checkbox>
            <Checkbox label="C3" name="category"></Checkbox>
            <Checkbox label="C4" name="category"></Checkbox>
          </Checkbox.Group>
        </Form.Item>
        {/* <Form.Item label="特殊资源">
          <Radio.Group value={this.state.form.resource}>
            <Radio value="线上品牌商赞助"></Radio>
            <Radio value="线下场地免费"></Radio>
          </Radio.Group>
        </Form.Item> */}
        <Form.Item label="Description">
          <Input type="textarea" value={this.state.form.description} onChange={this.onChange.bind(this, 'description')}></Input>
        </Form.Item>
        <Form.Item label="Image">
        <Upload
          className="upload-demo"
          drag
          action="//jsonplaceholder.typicode.com/posts/"
          multiple
        >
          <i className="el-icon-upload"></i>
          <div className="el-upload__text"><em>Drag file here，or click to upload</em></div>
        </Upload>
        </Form.Item>
        {/* <Layout.Row type = "flex" className="row-bg" justify="space-around">  */}
        <Form.Item>
          <Button type="primary" nativeType="submit">Create</Button>
          <Button>Cancel</Button>
        </Form.Item>
        {/* </Layout.Row > */}
      </Form>
      </Layout.Col>
      </Layout.Row >
      </div> 
    )
  }

}

export default Create;
