import React, {Component} from 'react';
import {Button, Form, Input, Layout} from 'element-react';
import axios from 'axios';

class Test extends React.Component {
    constructor(props){
        super(props);
        this.state = {
            form: {
                users: [],
                // id: 0,
                name: '',
                password: ''
            }
        }
    }
    componentDidMount(){
        axios.get("http://localhost:8181/api/")
            .then((res)=>{
                this.setState({
                    users:res.data,
                    id:0,
                    name:'',
                    password:''
                })
            })
    }

    onSubmit(evenet,id){
        console.log(id)
        evenet.preventDefault();
        if(id===0){
            axios.post("http://localhost:8181/api/",{
                name:this.state.name,
                password:this.state.password
            }).then(()=>{
                this.componentDidMount();
            })
        }else{
            axios.put("http://localhost:8181/api/",{
                id:id,
                name:this.state.name,
                password:this.state.password
            }).then(()=>{
                this.componentDidMount();
            })
        }
    }
    delete(id){
        axios.delete("http://localhost:8181/api/"+id)
            .then(()=>{
                this.componentDidMount();
            })
    }
    edit(id){
        axios.get("http://localhost:8181/api/"+id)
            .then((res)=>{
                this.setState({
                    id:res.data.id,
                    name:res.data.name,
                    password:res.data.password
                });
            })
    }

    onChange(key, value) {
        this.state.form[key] = value;
        const { id, name, password } = this.state;
        this.forceUpdate();
    }

    render(){
        return (
            <div>
                <Layout.Row type = "flex" className="row-bg" justify="space-around">
                    <h2>User Login</h2>
                </Layout.Row>
                <Layout.Row type = "flex" className="row-bg" justify="space-around">
                    <Layout.Col span ="8">
                    <Form model={this.state.form}  onSubmit={this.onSubmit.bind(this)}>
                    <Form.Item label="Name">
                        <Input value={this.state.form.name} onChange={this.onChange.bind(this, 'name')}></Input>
                    </Form.Item>
                    <Form.Item label="Password">
                        <Input value={this.state.form.password} onChange={this.onChange.bind(this, 'password')}></Input>
                    </Form.Item>
                    <br></br>
                    <Layout.Row type = "flex" className="row-bg" justify="space-around">
                        <Form.Item>
                            <Button type="primary" nativeType="submit">Login</Button>
                        </Form.Item>
                    </Layout.Row>
                    </Form>
                    </Layout.Col>
                </Layout.Row>
                <div className="col s6">
                    <table>
                        <thead>
                        <tr>
                            <th>Name</th>
                            <th>Password</th>
                            <th>Edit</th>
                            <th>Delete</th>
                        </tr>
                        </thead>


                        {/*<tbody>*/}
                        {/*{*/}
                        {/*    this.state.users.map(user =>*/}
                        {/*        <tr key={user.id}>*/}
                        {/*            <td>{user.name}</td>*/}

                        {/*            <td>{user.password}</td>*/}
                        {/*            <td>*/}
                        {/*                <button onClick={(e)=>this.edit(user.id)} className="btn waves-effect waves-light" type="submit" name="action">*/}
                        {/*                    <i className="material-icons ">edit</i>*/}
                        {/*                </button>*/}
                        {/*            </td>*/}
                        {/*            <td>*/}
                        {/*                <button onClick={(e)=>this.delete(user.id)} className="btn waves-effect waves-light " type="submit" name="action">*/}
                        {/*                    <i className="material-icons ">delete</i>*/}
                        {/*                </button>*/}
                        {/*            </td>*/}
                        {/*        </tr>*/}
                        {/*    )*/}
                        {/*};*/}


                        {/*</tbody>*/}
                    </table>
                </div>
            </div>

        );
    }
}

export default Test;
